import math
import matplotlib.pyplot as plt

class Poisson:
    def __init__(self, lamda=None, k=None, n=None):

        """
        k is the number of times an event occurs
        lamda is the average number of times an event occurs
        """
        self.lamda=lamda
        self.k=k
        self.n=n
        try:
            assert self.lamda > 0, 'lamda value is negative'
        except AssertionError as error:
            raise
        if self.lamda==None:
            self.lamda=self.calculate_lamda()
        elif self.k==None:
            self.k=self.calculate_k()
        elif self.n==None:
            self.k=self.calculate_n()
    def calculate_n(self):
        n= self.k / self.lamda
        self.n=n
        return n
    
    def calculate_k(self):
        k= self.n * self.lamda
        self.k=k
        return k
    
    def calculate_lamda(self):
        lamda=self.k / self.n
        self.lamda =lamda
        return lamda
    
    def pdf(self, x):
        f=(self.lamda**x) * math.exp(-self.lamda) / math.factorial(int(x))
        return f
    
    def plot_line_pdf(self):
        x=[]
        y=[]
        for i in range(10):
            x.append(i)
            y.append(self.pdf(i))
        plt.plot(x, y)
        plt.xlabel("X values")
        plt.ylabel("Probability")
        plt.title("Poisson distribution plot")
        plt.show()
        return x, y